    const PlayVideo = ({items, isLoading}) => {
        return (
      
          isLoading ?<h1>Loading...</h1> : (<section>
            
            {items.map((item) =>(
              
                 <VideoPlayer src={item.videoLink} key={item._id} poster={item.poster} width="700" height="450" ></VideoPlayer>
               
            )
            )}
                           
        </section>) 
      
        )
      }
      
//}
export default PlayVideo;
